mapper.select:
with TempResult as(SELECT id,name,address,phone,email,isnull(FollowerID,'')followerID, isnull((select top 1 username from TLG_User u where u.id=a.FollowerID),'')follower, isnull((select top 1 content from TLG_Gt g where g.applicantID=a.id),'')memo, isnull(Channel,'')channel,linktime,convert(varchar(10),nextTime,120)nextTime, isnull(status,1)status,convert(varchar(10),CreateTime,120)dt FROM dbo.TLG_AffiliateInfo a
isdelete=0
and FollowerID=#{FollowerID}
and CreateTime BETWEEN #{dtBegin} and #{dtEnd}
and charindex(#{keyWord},search)>0
and nextTime BETWEEN convert(varchar(10),getdate(),120)+' 00:00:00' and convert(varchar(10),getdate(),120)+' 23:59:59'
),TempCount as(select count(*) total from TempResult) SELECT * FROM TempResult, TempCount order by ${sort} offset #{pageSize}*(#{pageNum}-1) row fetch next #{pageSize} row only
